mapper.update:

	SELECT
		CASE
			WHEN user_is_admin = 'Y' THEN 'N'
			WHEN user_is_admin = 'N' THEN 'Y'
		END
	FROM user
	WHERE user_idx = #{user_idx}

UPDATE user
SET user_is_admin = #{user_is_admin}
WHERE user_idx = #{user_idx}

mapper.insert:
INSERT INTO notice
VALUES (
	null
	, #{notice_title}
	, #{notice_content}
	, now()
)

mapper.select:
SELECT notice_idx , notice_title , notice_content , notice_writeDate FROM notice
WHERE notice_idx = #{notice_idx }
ORDER BY notice_writeDate DESC

mapper.update:
UPDATE notice
SET 
	notice_title = #{notice_title }
	, notice_content = #{notice_content }
WHERE notice_idx = #{notice_idx };
