mapper.select:
-- selectEventList SELECT e.event_idx , e.event_title , e.event_content , e.event_writeDate , e.event_startDt , e.event_endDt , e.event_poster , e.event_thumbnail , e.event_category , ec.eventCate_subject FROM event e JOIN eventCate ec ON e.event_category = ec.eventCate_idx
WHERE eventCate_idx = #{eventCate_idx}
ORDER BY e.event_startDt DESC LIMIT #{startRow} , #{listLimit}

-- selectEventDetailList SELECT e.event_idx , e.event_title , e.event_content , e.event_writeDate , e.event_startDt , e.event_endDt , e.event_poster , e.event_thumbnail , e.event_category , ec.eventCate_subject FROM event e JOIN eventCate ec ON e.event_category = ec.eventCate_idx
WHERE eventCate_idx = #{eventCate_idx}
ORDER BY e.event_startDt DESC LIMIT #{startRow} , #{listLimit}
